import './App.css';
import { useEffect, useState } from 'react';
import { test, nameInfo, showSnatcherInfo, randomIDGenerator } from './Data'
import React from 'react';
import useWatchLocation from "./userWatch";
import { geolocationOptions } from "./Option";
import Location from "./Location";

function App() {

  const [phase, setPhase] = useState(0)
  const [time, setTime] = useState(0);
  const [isStart, setStart] = useState(0);//0 초기상태, 1 : Intro 창 출력, -1 
  const [isLoading, setLoading] = useState(false);

  const { location, cancelLocationWatch, error } = useWatchLocation(geolocationOptions);
  const [isLocation, setLocation] = useState([])
  const [isIDNumber, setIDNumber] = useState('')
  const [isIDString, setIDString] = useState('')

  const [input, setinput] = useState({
    a1:""
  })


  useEffect(() => {
    const getRandomID = randomIDGenerator()
    setIDNumber(getRandomID[0])
    setIDString(getRandomID[1])
  }, [])



  useEffect(() => {
    if (!location) {
      return;
    }
    // Cancel location watch after 3sec
    setTimeout(() => {
      cancelLocationWatch();
    }, 3000);
  }, [location, cancelLocationWatch]);


  useEffect(() => {
    let interval;
    interval = setInterval(() => {
      setTime((prevTime) => prevTime + 10);
    }, 10);
    return () => {
      clearInterval(interval);
    };
  }, [isStart]);

  useEffect(() => {
    function fetch() {
      if (isStart === -1) {
        setTime(0)
      }
      else if (isStart === 0) {
        if (Math.floor((time / 1000) % 60) === 5) {
          setStart(1);
        }
      }
      else {
        if (Math.floor((time / 1000) % 60) === 11) {
          // if (location === undefined) {
          //   alert('위치 정보를 찾을 수 없습니다. 당신은 Unqualified, Uneducated 합니다.')
          //   setStart(-1)
          //   setTime(0)
          // }
          // else {
          //   setLoading(true);
          // }

          // getUserLocation()
          setLoading(true);
        }
        if (Math.floor((time / 1000) % 60) === 16) {
          setPhase(1)
        }
        // if (Math.floor((time / 1000) % 60) === 15) {
        //   setCheck(true);
        // }
      }
    }
    fetch();
  }, [time, isStart]);



  // function getUserLocation() {
  //   if (!navigator.geolocation) {
  //       throw "위치 정보가 지원되지 않습니다.";
  //   }
  //   navigator.geolocation.watchPosition(success);
  // }

  // function success({coords,timestamp}){
  //   console.log(coords)
  //   const latitude =coords.latitude;
  //   const longitude = coords.longitude;
  //   console.log(latitude,longitude)
  //   setLocation([latitude,longitude])
  // }



  // showSnatcherInfo(true)

  // console.log(nameInfo)

	const handleText = (e) => {
		var { value, id } = e.target
		setinput({
			...input,
			[id]: value,
		});
	};

  return (
    <div className='default'>
      {isStart === -1 ? <div className='waitingPage' /> :
        isStart === 0 ? <div className='firstPage' />
          : phase === 0 ?
            <div className='backPage'>
              <div className='backPage_grid_1'><div className='mainTitle' /></div>
              <div className='backPage_grid_3'>
                {!isLoading ? <div className='loader4' /> :
                  <>
                    대상적합자 확인<br />
                    ID:{isIDNumber}<br />
                    keyValue:{isIDString}<br />
                    AccessPoint : 192.0.68.24<br />
                    {/* Latitude : {location.latitude}<br />
                    Longitude : {location.longitude}<br /> */}
                  </>
                }
              </div>
            </div>
            :
            <>
              <div className='secondPage'>
                <div className='backPage_grid_1-1'>
                  <div className='mainTitle2' />
                </div>
                <div className='backPage_grid_4'>
                   Last login: Thu Oct 22 15:26:25 on console
                   <div className='typing-demo'>{">"} 당신은 지금 2022 나Be한마당 행사에 참여 중입니까? [Y/N] </div>
                </div>
                <div style={{display:"flex",justifyContent:"space-around", width:"100%"}}>
                <input className='input_div' type="text" id="a1" value={input.a1} onChange={handleText} />
                <button className='button_1'>확인</button>
                </div>

              </div>
            </>
      }
    </div>
  );
}

