import './App.css';
import { useEffect, useState } from 'react';
import { test, nameInfo, showSnatcherInfo } from './Data'
import React from 'react';
import useWatchLocation from "./userWatch";
import { geolocationOptions } from "./Option";
import Location from "./Location";

function App() {

  const [phase, setPhase] = useState(0)
  const [time, setTime] = useState(0);
  const [isStart, setStart] = useState(false);
  const [isLoading, setLoading] = useState(false);
  const [isCheck, setCheck] = useState(false);
  const { location, cancelLocationWatch, error } = useWatchLocation(geolocationOptions);

  useEffect(() => {
    if (!location) return;
    // Cancel location watch after 3sec
    setTimeout(() => {
      cancelLocationWatch();    
    }, 3000);
  }, [location, cancelLocationWatch]);


  useEffect(() => {
    let interval;
    interval = setInterval(() => {
      setTime((prevTime) => prevTime + 10);
    }, 10);
    return () => {
      clearInterval(interval);
    };
  }, [isStart]);

  useEffect(() => {
    function fetch() {
      if (!isStart) {
        if (Math.floor((time / 1000) % 60) === 5) {
          setStart(true);
        }
      }
      if (Math.floor((time / 1000) % 60) === 11) {
        setLoading(true);
        getUserLocation()
      }
      if (Math.floor((time / 1000) % 60) === 15) {
        setCheck(true);
      }
    }
    fetch();
  }, [time, isStart]);

  const getDate = () => {
    const date = new Date()
    return date
  }


  function getUserLocation() {
    if (!navigator.geolocation) {
        throw "위치 정보가 지원되지 않습니다.";
    }
    navigator.geolocation.watchPosition(success);
  }

  function success({coords,timestamp}){
    console.log(coords)
    const latitude =coords.latitude;
    const longitude = coords.longitude;
    alert(latitude)
  }



  showSnatcherInfo(true)

  console.log(nameInfo)


  return (
    <div className='default'>

      {!isStart ? <div className='firstPage' />
        : phase === 0 ?
          <div className='backPage'>
            <div className='backPage_grid_1'><div className='mainTitle' /></div>
            <div className='backPage_grid_3'>
              {!isLoading ? <div className='loader4' /> :
                !isCheck ? <>
                  {/* 대상적합자 확인<br />
                  ID:3358<br />
                  192.0.68.24<br />
                  위도 32. 경도<br /> */}
                  <Location location={location.latitude} error={error} />
                  {}<br />
                </> : <div>
                  당신은 지금 2022 나Be한마당 행사에 참여 중입니까?
                  <div style={{ display: "flex", width: "100%", justifyContent: "space-around", margin:"30px auto" }}>
                    <button className='button_1'>Yes</button>
                    <button className='button_1'>No</button>
                  </div>
                </div>
              }
            </div>
          </div>
          : <>phase1</>
      }
    </div>
  );
}

export default App;
