import './App.css';
import { useEffect, useState } from 'react';
import { test, nameInfo, showSnatcherInfo } from './Data'
import React from 'react';

function App() {

  const [phase, setPhase] = useState(0)
  const [time, setTime] = useState(0);
  const [isStart, setStart] = useState(false);
  const [isLoading, setLoading] = useState(false);
  const [isCheck, setCheck] = useState(false);
  useEffect(() => {

  })

  useEffect(() => {
    let interval;
    interval = setInterval(() => {
      setTime((prevTime) => prevTime + 10);
    }, 10);
    return () => {
      clearInterval(interval);
    };
  }, [isStart]);

  useEffect(() => {
    function fetch() {
      if (!isStart) {
        if (Math.floor((time / 1000) % 60) === 5) {
          setStart(true);
        }
      }
      if (Math.floor((time / 1000) % 60) === 11) {
        setLoading(true);
      }
      if (Math.floor((time / 1000) % 60) === 15) {
        setCheck(true);
      }
    }
    fetch();
  }, [time, isStart]);





  showSnatcherInfo(true)

  console.log(nameInfo)

  return (
    <div className='default'>
      {time}
      {!isStart ? <div className='firstPage' />
        : phase === 0 ?
          <div className='backPage'>
            <div className='backPage_grid_1'><div className='mainTitle' /></div>
            <div className='backPage_grid_3'>
              {!isLoading ? <div className='loader4' /> :
                !isCheck ? <>
                  대상적합자 확인 : 현위치 GPS —----<br />
                  ID:3358<br />
                  192.0.68.24<br />
                  위도 32. 경도<br />
                  2033. 04. 01 14:11예정<br />
                </> : <>
                  당신은 지금 2022 나Be한마당 행사에 참여 중입니까?
                  <div style={{ display: "flex", width: "100%", justifyContent: "space-around" }}>
                    <button>Yes</button>
                    <button>No</button>
                  </div>
                </>
              }
            </div>
          </div>
          : <>phase1</>
      }
    </div>
  );
}

export default App;
